package com.info.basic;

public class InhhancedLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String array[] = {"Ron", "Harry", "Hermoine"};
		 
        //enhanced for loop
		
		System.out.println(array);
        for (String x:array)
        {
            System.out.println(x);
        }
	}

}
